
//Literal is a constant value assigned to a variable....


public class Literals {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=10;
		char b='a';
		float c=1.0f;
		double d=1.00d;
		boolean e=true;
		String str="Arya";
		
		System.out.println(b); 
		System.out.println(e);
		System.out.println(str);

	}

}
